"use strict";

var fluid = require("infusion");
require("xrandr");

var gpii = fluid.registerNamespace("gpii");
fluid.require("%universal");

fluid.defaults("gpii.platformReporter.linux", {
    gradeNames: ["gpii.platformReporter"],
    invokers: {
        getOSspecifics: "gpii.platformReporter.linux.getOSspecifics"
    }
});

gpii.platformReporter.linux.getOSspecifics = function () {
    return gpii.xrandr.getAllScreenResolutions();
};
