/*global require */

"use strict";

var fluid = require("universal");
var gpii = fluid.registerNamespace("gpii");
var nodeProcesses = require("./nodeprocesses/build/Release/nodeprocesses.node");

var gpii = fluid.registerNamespace("gpii");

fluid.defaults("gpii.processes.linux", {
    gradeNames: ["gpii.processes"],
    invokers: {
        getProcessList: {
            funcName: "gpii.processes.linux.getProcessList",
            args: []
        }
    }
});

// Return a list of processes -- a snapshot of the current processes.
gpii.processes.linux.getProcessList = function () {
    return nodeProcesses.getProcesses();
};
