#!/usr/bin/env node

var xrandr = require('./build/Release/nodexrandr.node');

var r1 = xrandr.getDisplays();
console.log("getDisplays = " + JSON.stringify(r1, null, 2));

var r2 = xrandr.getBrightness();
console.log("getBrightness = " + JSON.stringify(r2, null, 2));

xrandr.setBrightness(10);

var r3 = xrandr.getBrightness();
console.log("getBrightness = " + JSON.stringify(r3, null, 2));

xrandr.setBrightness(r2.value);
var r4 = xrandr.getBrightness();
console.log("getBrightness = " + JSON.stringify(r4, null, 2));

var width = r1[0].resolution.width
var height = r1[0].resolution.height

xrandr.setScreenResolution(800,600);
var r5 = xrandr.getDisplays();
console.log("getDisplays = " + JSON.stringify(r5, null, 2));

xrandr.setScreenResolution(width,height);
var r6 = xrandr.getDisplays();
console.log("getDisplays = " + JSON.stringify(r6, null, 2));
