#!/usr/bin/env node

var xrandr = require('./build/Release/nodexrandr.node');

var r1 = xrandr.getDisplays();
console.log("getDisplays = " + JSON.stringify(r1, null, 2));

var r2 = xrandr.getBrightness();
console.log("getBrightness = " + JSON.stringify(r2, null, 2));

xrandr.setBrightness(10);

r3 = xrandr.getBrightness();
console.log("getBrightness = " + JSON.stringify(r3, null, 2));

xrandr.setBrightness(r2.value);
r4 = xrandr.getBrightness();
console.log("getBrightness = " + JSON.stringify(r4, null, 2));
